const indexBody = document.getElementById('index_body')

const openPopUp = document.getElementById('open_pop_up');
const closePopUp = document.getElementById('close_pop_up');
const popUp = document.getElementById('pop_up');

const openAddFormSong = document.getElementById('open_add_form_song')
const openAddFormSinger = document.getElementById('open_add_form_singer')
const openAddFormAlbum = document.getElementById('open_add_form_album')

const addFormSong = document.getElementById('form_song')
const addFormSinger = document.getElementById('form_singer')
const addFormAlbum = document.getElementById('form_album')



openPopUp.addEventListener('click', function (e) {
    e.preventDefault();
    addFormSong.classList.add('active');
    popUp.classList.add('active');
    addFormSong.classList.add('active');
    openAddFormSong.classList.add('active');
    indexBody.classList.add('active');
    return false;
})

closePopUp.addEventListener('click', function (e) {
    e.preventDefault();
    indexBody.classList.remove('active');
    addFormSong.classList.remove('active');
    addFormSinger.classList.remove('active');
    addFormAlbum.classList.remove('active');
    openAddFormSong.classList.remove('active');
    openAddFormSinger.classList.remove('active');
    openAddFormAlbum.classList.remove('active');
    popUp.classList.remove('active');
    return false;
})


openAddFormSong.addEventListener('click', function (x) {
    x.preventDefault();
    addFormSong.classList.add('active');
    addFormSinger.classList.remove('active');
    addFormAlbum.classList.remove('active');
    openAddFormSong.classList.add('active');
    openAddFormSinger.classList.remove('active');
    openAddFormAlbum.classList.remove('active');
    return false;
})

openAddFormSinger.addEventListener('click', function (x) {
    x.preventDefault();
    openAddFormSinger.classList.add('active');
    addFormSinger.classList.add('active');
    addFormSong.classList.remove('active');
    addFormAlbum.classList.remove('active');
    openAddFormAlbum.classList.remove('active');
    openAddFormSong.classList.remove('active');
    return false;
})

openAddFormAlbum.addEventListener('click', function (x) {
    x.preventDefault();
    openAddFormAlbum.classList.add('active');
    addFormAlbum.classList.add('active');
    addFormSong.classList.remove('active');
    addFormSinger.classList.remove('active');
    openAddFormSinger.classList.remove('active');
    openAddFormSong.classList.remove('active');
    return false;
})


