export default {
    render(templateName, data) {
        templateName = templateName + 'jopa';

        const templateElement = document.getElementById('test');
        const templateSource = templateElement.innerHTML;
        const rendrFn = Handlebars.compile(templateSource);


        return renderFn(data);
    }
}



